    const createKelas = (e) => {
        e.preventDefault();
        const data = {};
        for (const el of e.target.elements) {
            if (el.name !== "") data[el.name] = el.value;
        }

        axios.post(url_by_institute, {
                "processDefinitionId":  insert_class,
                "returnVariables": true,
                "variables": [
                    {
                        "name": "validasi",
                        "type": "json",
                        "value": {
                            "data": {
                                "academic_year_id": "required",
                                "class": "required",
                                "sub_class": "required",
                                "sub_sub_class": "required",
                                "class_location": "required",
                                "calss_advisor_id": "required",
                                "image": "required",
                                "image_type": "required",
                                "nama_folder": "required"
                            },
                            "academic_year_id": data.tahun_akademik,
                            "class": data.nama_kelas,
                            "sub_class": data.sub_kelas,
                            "sub_sub_class": '-',
                            "class_location": data.lokasi_kelas,
                            "calss_advisor_id": data.wali_kelas, //api
                            "image": "",
                            "image_type": "png",
                            "nama_folder": "image_class"
                        }
                    },
                    {
                        "name": "class",
                        "type": "json",
                        "value": {
                            "tbl_name": "x_academic_classModel",
                            "tbl_coloumn": {
                                "academic_year_id": data.tahun_akademik,
                                "class": data.nama_kelas,
                                "sub_class": data.sub_kelas,
                                "sub_sub_class": '-',
                                "class_location": data.lokasi_kelas,
                                "calss_advisor_id": data.wali_kelas,
                            }
                        }
                    },
                    {
                        "name": "upload_image",
                        "type": "json",
                        "value": {
                            "image": "",
                            "image_type": "png",
                            "nama_folder": "image_class"
                        }
                    }
                ]
            }, {
                headers: {
                    "Content-Type": "application/json",
                    "Authorization": "Basic YWRtaW46TWFuYWczciE="
                }
            }
        ).then(function (response) {
            const resCode = response.data.variables[4].value
            if (resCode == 200) {
                setIsViewCreate(false)
                setIsViewKelas(true)
                notification.success({
                    message: "Sukses",
                    description: 'Data kelas berhasil ditambahkan',
                    placement: 'top'
                })
            } else {
                notification.error({
                    message: "Error",
                    description: 'Gagal menambahkan data kelas, mohon kontak admin.',
                    placement: 'top'
                })
            }
        }).catch(error => {
            alert(error)
        });
    }

    const editKelas = (e) => {
        e.preventDefault();
        const data = {};
        for (const el of e.target.elements) {
            if (el.name !== "") data[el.name] = el.value;
        }
        axios.post(url_by_institute, {
                "processDefinitionId": global_update,
                "returnVariables": true,
                "variables": [
                    {
                        "name": "global_updatedata",
                        "type": "json",
                        "value": {
                            "tbl_name": "x_academic_classModel",
                            "id": selectedUser.id,
                            "tbl_coloumn": {
                                "academic_year_id": data.tahun_akademik,
                                "class": data.nama_kelas,
                                "sub_class": data.sub_kelas,
                                "class_location": data.lokasi_kelas,
                                "calss_advisor_id": data.wali_kelas,
                            }
                        }
                    }
                ]
            }, {
                headers: {
                    "Content-Type": "application/json",
                    "Authorization": "Basic YWRtaW46TWFuYWczciE="

                }
            }
        ).then(function (response) {
            console.log(response);
            const resCode = JSON.parse(response.data.variables[2].value)
            const statusCode = resCode.status;
            if (statusCode == 'success') {
                setIsViewEdit(false)
                setIsViewKelas(true)
                notification.success({
                    message: "Sukses",
                    description: 'Data kelas berhasil di update',
                    placement: 'top'
                })
            } else {
                notification.error({
                    message: "Error",
                    description: 'Gagal menambahkan update kelas, mohon kontak admin.',
                    placement: 'top'
                })
            }
        }).catch(error => {
            alert(error)
        });
    }

    const deleteKelas = (record) => {
        const dateNow = new Date().toLocaleString();
        Swal.fire({
            title: 'Apakah anda yakin menghapus data?',
            text: "Anda tidak dapat mengembalikan data yang sudah terhapus",
            icon: 'warning',
            showCancelButton: true,
            confirmButtonColor: '#3085d6',
            cancelButtonColor: '#d33',
            cancelButtonText: 'Batalkan',
            confirmButtonText: 'Hapus'
        }).then((result) => {
            if (result.isConfirmed) {
                axios.post(url_by_institute, {
                        "processDefinitionId": global_update,
                        "returnVariables": true,
                        "variables": [
                            {
                                "name": "global_updatedata",
                                "type": "json",
                                "value": {
                                    "tbl_name": "x_academic_classModel",
                                    "id": record.id,
                                    "tbl_coloumn": {
                                        "deleted_at": dateNow
                                    }
                                },
                            }
                        ]
                    }, {
                        headers: {
                            "Content-Type": "application/json",
                            "Authorization": "Basic YWRtaW46TWFuYWczciE="
                        }
                    }
                ).then(function (response) {
                    setRefreshState(true);
                    Swal.fire(
                        'Data telah terhapus!',
                        'Menghapus data kelas ' + record.namaKelas,
                        'success'
                    )
                })
            }
        })

    }


